//second page
class SongDetailsPage extends StatelessWidget {
  final Map<String, String> song;
  final String tag;

  const SongDetailsPage({super.key, required this.song, required this.tag});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: Text("Song Details")),
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            Hero(
              tag: tag,
              child: ClipRRect(
                borderRadius: BorderRadius.circular(12.0),
                child: SizedBox(
                  height: 300,
                  width: 400,
                  child: Image.network(
                    song["albumCoverUrl"]!,
                    fit: BoxFit.cover,
                  ),
                ),
              ),
            ),
            const SizedBox(height: 20),
            Text(
              song["title"]!,
              style: const TextStyle(fontSize: 24, fontWeight: FontWeight.bold),
            ),
            const SizedBox(height: 25),
            Row(
              mainAxisAlignment: MainAxisAlignment.center,
              children: [
                Icon(Icons.skip_previous, size: 40),
                SizedBox(width: 20),
                Icon(Icons.play_circle_fill, size: 60, color: Colors.blue),
                SizedBox(width: 20),
                Icon(Icons.skip_next, size: 40),
              ],
            ),
          ],
        ),
      ),
    );
  }
}

